// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract EscrowDst {
    address public owner;

    mapping(uint256 => bool) public processedSwaps;

    event Unlocked(uint256 indexed swapId, address indexed to, address token, uint256 amount);

    constructor() {
        owner = msg.sender;
    }

    function unlock(uint256 _swapId, address _to, address _token, uint256 _amount) external {
        require(msg.sender == owner, "Only owner can unlock funds");
        require(!processedSwaps[_swapId], "Swap already processed");

        processedSwaps[_swapId] = true;
        IERC20(_token).transfer(_to, _amount);

        emit Unlocked(_swapId, _to, _token, _amount);
    }
}

