// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract EscrowSrc {
    address public owner;
    uint256 public swapCounter;

    struct Swap {
        address from;
        address to;
        address token;
        uint256 amount;
        bool completed;
    }

    mapping(uint256 => Swap) public swaps;

    event Locked(uint256 indexed swapId, address indexed from, address indexed to, address token, uint256 amount);

    constructor() {
        owner = msg.sender;
    }

    function lock(address _to, address _token, uint256 _amount) external {
        IERC20(_token).transferFrom(msg.sender, address(this), _amount);
        swaps[swapCounter] = Swap(msg.sender, _to, _token, _amount, false);
        emit Locked(swapCounter, msg.sender, _to, _token, _amount);
        swapCounter++;
    }
}

